 208	 /**
 209	 * Handle intent to get playlist ID
 210	 */
 211	 private void handleIntent() {
 212	 Intent intent = getIntent();
 213	 long playlistId = intent.getLongExtra(EXTRA_PLAYLIST_ID, -1);
 214	 
 215	 if (playlistId != -1) {
 216	 viewModel.loadPlaylist(playlistId);
 217	 } else {
 218	 Toast.makeText(this, "Invalid playlist ID", Toast.LENGTH_SHORT).show();
 219	 finish();
 220	 }
 221	 }
