 157	 private List<SearchResult> filterResults(String query, FilterType filter) {
 158	 List<SearchResult> filtered = new ArrayList<>();
 159	 
 160	 for (SearchResult result : allResults) {
 161	 // Apply text filter
 162	 if (!result.matchesQuery(query)) {
 163	 continue;
 164	 }
 165	 
 166	 // Apply type filter
 167	 switch (filter) {
 168	 case ALL:
 169	 filtered.add(result);
 170	 break;
 171	 case SONGS:
 172	 if (result.getType() == SearchResult.Type.SONG) {
 173	 filtered.add(result);
 174	 }
 175	 break;
 176	 case ARTISTS:
 177	 if (result.getType() == SearchResult.Type.ARTIST) {
 178	 filtered.add(result);
 179	 }
 180	 break;
 181	 case PLAYLISTS:
 182	 if (result.getType() == SearchResult.Type.PLAYLIST) {
 183	 filtered.add(result);
 184	 }
 185	 break;
 186	 }
 187	 }
 188	 
 189	 return filtered;
 190	 }
