 29	
 30	 // Xử lý nút Register
 31	 btnRegister.setOnClickListener(v -> {
 32	 String username = edtUsername.getText().toString().trim();
 33	 String password = edtPassword.getText().toString().trim();
 34	 String confirmPassword = edtConfirmPassword.getText().toString().trim();
 35	
 36	 if (username.isEmpty() || password.isEmpty() || confirmPassword.isEmpty()) {
 37	 Toast.makeText(this, "Vui lòng điền đầy đủ thông tin", Toast.LENGTH_SHORT).show();
 38	 } else if (!password.equals(confirmPassword)) {
 39	 Toast.makeText(this, "Mật khẩu không khớp", Toast.LENGTH_SHORT).show();
 40	 } else {
 41	 // Sau này có thể thêm lưu DB ở đây
 42	 Toast.makeText(this, "Đăng ký thành công!", Toast.LENGTH_SHORT).show();
 43	 startActivity(new Intent(RegisterActivity.this, LoginActivity.class));
 44	 finish();
 45	 }
