 10	import java.util.List;
 11	import java.util.concurrent.ExecutorService;
 12	import java.util.concurrent.Executors;
 13	import java.util.concurrent.Future;
 14	
 15	public class UserRepository {
 16	 
 17	 private UserDao userDao;
 18	 private ExecutorService executor;
 19	 
 20	 public UserRepository(Application application) {
 21	 AppDatabase database = AppDatabase.getInstance(application);
 22	 userDao = database.userDao();
 23	 executor = Executors.newFixedThreadPool(4);
 24	 }
 25	 
 26	 // Create
 27	 public Future<Long> insert(User user) {
 28	 return executor.submit(() -> userDao.insert(user));
 29	 }
 30	 
 31	 // Read
 32	 public LiveData<User> getUserById(long userId) {
 33	 return userDao.getUserById(userId);
 34	 }
 35	 
 36	 public Future<User> getUserByIdSync(long userId) {
 37	 return executor.submit(() -> userDao.getUserByIdSync(userId));
 38	 }
 39	 
 40	 public Future<User> getUserByUsername(String username) {
 41	 return executor.submit(() -> userDao.getUserByUsername(username));
 42	 }
 43	 
 44	 public Future<User> getUserByEmail(String email) {
 45	 return executor.submit(() -> userDao.getUserByEmail(email));
 46	 }
 47	 
 48	 public Future<User> authenticateUser(String username, String passwordHash) {
 49	 return executor.submit(() -> userDao.authenticateUser(username, passwordHash));
 50	 }
 51	 
 52	 public LiveData<List<User>> getAllUsers() {
 53	 return userDao.getAllUsers();
 54	 }
 55	 
 56	 public LiveData<List<User>> searchUsers(String query) {
 57	 return userDao.searchUsers(query);
 58	 }
 59	 
 60	 // Update
 61	 public Future<Void> update(User user) {
 62	 return executor.submit(() -> {
 63	 userDao.update(user);
 64	 return null;
 65	 });
 66	 }
 67	 
 68	 // Delete
 69	 public Future<Void> delete(User user) {
 70	 return executor.submit(() -> {
 71	 userDao.delete(user);
 72	 return null;
 73	 });
 74	 }
 75	 
 76	 // Validation
 77	 public Future<Boolean> isUsernameExists(String username) {
 78	 return executor.submit(() -> userDao.checkUsernameExists(username) > 0);
 79	 }
 80	 
 81	 public Future<Boolean> isEmailExists(String email) {
 82	 return executor.submit(() -> userDao.checkEmailExists(email) > 0);
 83	 }
 84	 
 85	 public void shutdown() {
 86	 if (executor != null) {
 87	 executor.shutdown();
 88	 }
 89	 }
 90	} ...
