 93	 private void setupRecyclerView() {
 94	 adapter = new SearchAdapter(requireContext());
 95	 adapter.setOnSearchResultClickListener(this);
 96	 
 97	 recyclerSearchResults.setLayoutManager(new LinearLayoutManager(requireContext()));
 98	 recyclerSearchResults.setAdapter(adapter);
