 101	 private void setupViewModel() {
 102	 viewModel = new ViewModelProvider(this).get(SearchViewModel.class);
 103	 }
 104	
 105	 private void setupSearchInput() {
 106	 editTextSearch.addTextChangedListener(new TextWatcher() {
 107	 @Override
 108	 public void beforeTextChanged(CharSequence s, int start, int count, int after) {}
 109	
 110	 @Override
 111	 public void onTextChanged(CharSequence s, int start, int before, int count) {}
 112	
 113	 @Override
 114	 public void afterTextChanged(Editable s) {
 115	 String query = s.toString().trim();
 116	 if (!query.equals(currentQuery)) {
 117	 currentQuery = query;
 118	 viewModel.search(query);
 119	 }
 120	 }
 121	 });
 122	 }
 123	
 124	 private void setupFilterChips() {
 125	 chipGroupFilters.setOnCheckedStateChangeListener((group, checkedIds) -> {
 126	 if (checkedIds.isEmpty()) return;
 127	 
 128	 int checkedId = checkedIds.get(0);
 129	 SearchViewModel.FilterType filter;
 130	 
 131	 if (checkedId == R.id.chip_all) {
 132	 filter = SearchViewModel.FilterType.ALL;
 133	 } else if (checkedId == R.id.chip_songs) {
 134	 filter = SearchViewModel.FilterType.SONGS;
 135	 } else if (checkedId == R.id.chip_artists) {
 136	 filter = SearchViewModel.FilterType.ARTISTS;
 137	 } else if (checkedId == R.id.chip_playlists) {
 138	 filter = SearchViewModel.FilterType.PLAYLISTS;
 139	 } else {
 140	 filter = SearchViewModel.FilterType.ALL;
 141	 }
 142	 
 143	 viewModel.setFilter(filter);
 144	 });
 145	 }
 146	
 147	 private void observeViewModel() {
 148	 // Observe search results
 149	 viewModel.getSearchResults().observe(getViewLifecycleOwner(), results -> {
 150	 if (results != null) {
 151	 adapter.setSearchResults(results);
 152	 updateResultsCount(results.size());
 153	 updateUIState(results.isEmpty() && !currentQuery.isEmpty(), false);
 154	 }
 155	 });
 156	
 157	 // Observe loading state
 158	 viewModel.getIsLoading().observe(getViewLifecycleOwner(), isLoading -> {
 159	 if (isLoading != null) {
 160	 layoutLoading.setVisibility(isLoading ? View.VISIBLE : View.GONE);
 161	 if (isLoading) {
 162	 layoutEmpty.setVisibility(View.GONE);
 163	 recyclerSearchResults.setVisibility(View.GONE);
 164	 textResultsCount.setVisibility(View.GONE);
 165	 }
 166	 }
 167	 });
 168	
 169	 // Observe current query
 170	 viewModel.getCurrentQuery().observe(getViewLifecycleOwner(), query -> {
 171	 if (query != null) {
 172	 currentQuery = query;
 173	 // Don't show empty state for empty query - let search results handle it
 174	 }
 175	 });
 176	
 177	 // Observe errors
 178	 viewModel.getError().observe(getViewLifecycleOwner(), error -> {
 179	 if (error != null && !error.isEmpty()) {
 180	 showToast("Search error: " + error);
 181	 }
 182	 });
 183	 }
 184	
 185	 private void updateResultsCount(int count) {
 186	 if (count > 0 && !currentQuery.isEmpty()) {
 187	 textResultsCount.setVisibility(View.VISIBLE);
 188	 if (count == 1) {
 189	 textResultsCount.setText(getString(R.string.search_results_count_single));
 190	 } else {
 191	 textResultsCount.setText(getString(R.string.search_results_count, count));
 192	 }
 193	 } else {
 194	 textResultsCount.setVisibility(View.GONE);
 195	 }
 196	 }
 197	
 198	 private void updateUIState(boolean isEmpty, boolean isInitialState) {
 199	 if (isEmpty) {
 200	 // Show empty results state
 201	 layoutEmpty.setVisibility(View.VISIBLE);
 202	 recyclerSearchResults.setVisibility(View.GONE);
 203	 textEmptyTitle.setText(getString(R.string.search_empty_title));
 204	 textEmptySubtitle.setText(getString(R.string.search_empty_subtitle));
 205	 } else if (isInitialState) {
 206	 // Show initial state (no search yet)
 207	 layoutEmpty.setVisibility(View.VISIBLE);
 208	 recyclerSearchResults.setVisibility(View.GONE);
 209	 textEmptyTitle.setText(getString(R.string.search_empty_query_title));
 210	 textEmptySubtitle.setText(getString(R.string.search_empty_query_subtitle));
 211	 } else {
 212	 // Show results
 213	 layoutEmpty.setVisibility(View.GONE);
 214	 recyclerSearchResults.setVisibility(View.VISIBLE);
 215	 }
 216	 }
