 47	 
 48	 // Password Hashing
 49	 public static String hashPassword(String password) {
 50	 try {
 51	 MessageDigest md = MessageDigest.getInstance("SHA-256");
 52	 byte[] hash = md.digest(password.getBytes());
 53	 StringBuilder hexString = new StringBuilder();
 54	 
 55	 for (byte b : hash) {
 56	 String hex = Integer.toHexString(0xff & b);
 57	 if (hex.length() == 1) {
 58	 hexString.append('0');
 59	 }
 60	 hexString.append(hex);
 61	 }
 62	 
 63	 return hexString.toString();
 64	 } catch (NoSuchAlgorithmException e) {
 65	 throw new RuntimeException("SHA-256 algorithm not available", e);
 66	 }
 67	 }
 68	 
 69	 // Validation
 70	 public static boolean isValidUsername(String username) {
 71	 return username != null && 
 72	 username.length() >= 3 && 
 73	 username.length() <= 50 && 
 74	 username.matches("^[a-zA-Z0-9_]+$");
 75	 }
