 64	 /**
 65	 * Login user with username and password
 66	 */
 67	 public void login(String username, String password) {
 68	 // Reset previous states
 69	 errorMessage.setValue(null);
 70	 loginSuccess.setValue(false);
 71	 
 72	 // Validate input
 73	 if (TextUtils.isEmpty(username)) {
 74	 errorMessage.setValue("Vui lòng nhập tên đăng nhập");
 75	 return;
 76	 }
 77	 
 78	 if (TextUtils.isEmpty(password)) {
 79	 errorMessage.setValue("Vui lòng nhập mật khẩu");
 80	 return;
 81	 }
 82	 
 83	 // Show loading
 84	 isLoading.setValue(true);
 85	 
 86	 // Hash password and authenticate
 87	 String passwordHash = AuthManager.hashPassword(password);
 88	 
 89	 // Perform authentication in background thread
 90	 Future<User> authFuture = userRepository.authenticateUser(username, passwordHash);
 91	 
 92	 // Handle result in background thread to avoid blocking UI
 93	 new Thread(() -> {
 94	 try {
 95	 User user = authFuture.get();
 96	 
 97	 // Post results to main thread
 98	 if (user != null) {
 99	 // Login successful
 100	 authManager.saveUserSession(user.getId(), user.getUsername());
 101	 currentUser.postValue(user);
 102	 loginSuccess.postValue(true);
 103	 } else {
 104	 // Login failed
 105	 errorMessage.postValue("Tên đăng nhập hoặc mật khẩu không đúng");
 106	 }
 107	 } catch (ExecutionException | InterruptedException e) {
 108	 errorMessage.postValue("Lỗi kết nối. Vui lòng thử lại");
 109	 } finally {
 110	 isLoading.postValue(false);
 111	 }
 112	 }).start();
 113	 }
...
 132	 
 133	 // Check if username or email already exists
 134	 new Thread(() -> {
 135	 try {
 136	 Future<Boolean> usernameExistsFuture = userRepository.isUsernameExists(username);
 137	 Future<Boolean> emailExistsFuture = userRepository.isEmailExists(email);
 138	 
 139	 boolean usernameExists = usernameExistsFuture.get();
 140	 boolean emailExists = emailExistsFuture.get();
 141	 
 142	 if (usernameExists) {
 143	 errorMessage.postValue("Tên đăng nhập đã tồn tại");
 144	 isLoading.postValue(false);
 145	 return;
 146	 }
 147	 
 148	 if (emailExists) {
 149	 errorMessage.postValue("Email đã được sử dụng");
 150	 isLoading.postValue(false);
 151	 return;
 152	 }
 153	 
 154	 // Create new user
 155	 String passwordHash = AuthManager.hashPassword(password);
 156	 User newUser = new User(username, displayName, email, passwordHash);
 157	 
 158	 Future<Long> insertFuture = userRepository.insert(newUser);
 159	 long userId = insertFuture.get();
 160	 
 161	 if (userId > 0) {
 162	 // Registration successful
 163	 newUser.setId(userId);
 164	 registerSuccess.postValue(true);
 165	 } else {
 166	 errorMessage.postValue("Đăng ký thất bại. Vui lòng thử lại");
 167	 }
 168	 
 169	 } catch (ExecutionException | InterruptedException e) {
 170	 errorMessage.postValue("Lỗi kết nối. Vui lòng thử lại");
 171	 } finally {
 172	 isLoading.postValue(false);
 173	 }
 174	 }).start();
 175	 }
